class Solution {
public:
    bool div(const vector<int>& s, int z) {
        int x = 0;
        for (auto y : s) {
            x = x*10 + y;
            x %= z;
        }

        return x == 0;
    }

    void sub(vector<int>& a) {
        int x = (a.size()-1)/2;
        int xx = a.size()-1-x;
        while (a[x] == 0) {
            a[x] = 9;
            a[xx] = 9;
            --x;
            ++xx;
        }
        --a[x];
        if (x < xx) {
            --a[xx];
        }
    }

    string tos(const vector<int>& a) {
        string r;
        for (auto c : a) {
            r += '0'+c;
        }
        return r;
    }

    string largestPalindrome(int n, int k) {
        for (int i=1;;++i) {
            vector<int> a(n, 9);
            if (k == 5) {
                a[0] = 5;
                a.back() = 5;
            }
            if (k%2 == 0) {
                int x = 0;
                int xx = n-1;
                for (int ii =0; ii<i; ++ii) {
                    a[x] = 8;
                    a[xx] = 8;
                    ++x;
                    --xx;
                }
            }

            for (int j=0; j*n < 2000000; ++j) {
                if (div(a, k)) {
                    return tos(a);
                }
                sub(a);
            }
        }
        std::abort();
    }
};
