class Solution {
    public String largestPalindrome(int n, int k) {
        StringBuilder sb = new StringBuilder();
        if (k == 1 || k == 3) {
            for (int i = 0; i < n; i++)
                sb.append('9');
            return sb.toString();
        }

        if (k == 2) {
            if (n == 1)
                return "8";
            sb.append("8");
            for (int i = 0; i < n - 2; i++)
                sb.append("9");
            sb.append("8");
            return sb.toString();
        }

        if (k == 4) {
            if (n == 1)
                return "8";
            if (n == 2)
                return "88";
            if (n == 3)
                return "888";
            if (n == 4)
                return "8888";
            sb.append("88");
            for (int i = 0; i < n - 4; i++)
                sb.append("9");
            sb.append("88");

            return sb.toString();
        }

        if (k == 5) {
            if (n == 1)
                return "5";
            if (n == 2)
                return "55";
            sb.append("5");
            for (int i = 0; i < n - 2; i++)
                sb.append("9");
            sb.append("5");
            return sb.toString();
        }

        if (k == 6) {
            if (n == 1)
                return "6";
            if (n == 2)
                return "66";
            int sum = 0;
            boolean odd = n % 2 != 0;
            for (int i = 0; i < (n + 1) / 2; i++)   {
                for (int j = 9; j >= 0; j--)    {
                    if (i == 0 && j % 2 != 0)
                        continue;
                    if (i == 0 && j % 2 == 0)   {
                        sum += 2 * j;
                        sb.append((char)('0' + j));
                        break;
                    }
                    if (i == (n + 1) / 2 - 1)   {
                        if (odd)    {
                            if ((sum + j) % 3 != 0)
                                continue;
                            sum += j;
                            sb.append((char)('0' + j));
                            break;
                        }
                        else    {
                            if ((sum + 2 * j) % 3 != 0)
                                continue;
                            sum += 2 * j;
                            sb.append((char)('0' + j));
                            break;
                        }
                    }

                    sum += 2 * j;
                    sb.append((char)('0' + j));
                    break;
                }

            }

            if (odd)
                return sb.toString() + new StringBuilder(sb.substring(0, sb.length() - 1)).reverse().toString();
            return sb.toString() + sb.reverse().toString();
        }

        if (k == 7) {
            if (n == 1)
                return "7";
            if (n == 2)
                return "77";
            if (n == 3)
                return "959";
            if (n == 4)
                return "9779";
            if (n == 5)
                return "99799";
            // if (n == 6)
            //     return "999999";
            // if (n == 7)
            //     return "9994999";
            // if (n == 8)
            //     return "99944999";
            // if (n == 9)
            //     return "999969999";
            // if (n == 10)
            //     return "9999449999";
            // if (n == 11)
            //     return "99999499999";
            // if (n == 12)
            //     return "999999999999";
            // if (n == 13)
            //     return "9999997999999";
            // if (n == 14)
            //     return "99999977999999";
            // if (n == 15)
            //     return "999999959999999";
            // if (n == 16)
            //     return "9999999779999999";
            // if (n == 17)
            //     return "99999999799999999";
            // if (n == 18)
            //     return "999999999999999999";
            // if (n == 19)
            //     return "9999999994999999999";
            // if (n == 20)
            //     return "99999999944999999999";
            // if (n == 21)
            //     return "999999999969999999999";
            // if (n == 22)
            //     return "9999999999449999999999";
            // if (n == 23)
            //     return "99999999999499999999999";
            // if (n == 24)
            //     return "999999999999999999999999";
            // if (n == 25)
            //     return "9999999999997999999999999";
            int[] digits = new int[] {9,4,4,6,4,4,9,7,7,5,7,7};
            int idx = (n - 6) % 12;
            if (n % 2 == 0) {
                for (int i = 0; i < n / 2 - 1; i++)
                    sb.append('9');
                sb.append((char)('0' + digits[idx]));
                sb.append((char)('0' + digits[idx]));
                for (int i = 0; i < n / 2 - 1; i++)
                    sb.append('9');
                return sb.toString();
            }

            for (int i = 0; i < n / 2; i++)
                    sb.append('9');
            sb.append((char)('0' + digits[idx]));
            for (int i = 0; i < n / 2; i++)
                sb.append('9');
            return sb.toString();
        }

        if (k == 8) {
            if (n == 1)
                return "8";
            if (n == 2)
                return "88";
            if (n == 3)
                return "888";
            if (n == 4)
                return "8888";
            if (n == 5)
                return "88888";
            if (n == 6)
                return "888888";
            sb.append("888");
            for (int i = 0; i < n - 6; i++)
                sb.append("9");
            sb.append("888");
            return sb.toString();
        }

        if (k == 9) {
            for (int i = 0; i < n; i++)
                sb.append("9");
            return sb.toString();
        }

        return "";
    }

    String reverse(String s)    {
        return new StringBuilder(s).reverse().toString();
    }
}
