class Solution {
public:
    bool ispali(string &s)
    {
        int n = s.size();
        for(int i=0;i<n;i++)
        {
            if(s[i]!=s[n-i-1])
                return false;
        }
        return true;
    }
    int Remn(string str, int R)
    {
        int len = str.length();

    int Num, Rem = 0;


    for (int i = 0; i < len; i++) {

        Num = Rem * 10 + (str[i] - '0');
        Rem = Num % R;
    }

    return Rem; 
    }
    string largestPalindrome(int n, int k) {
        if(k==1 || k==3 || k==9)
        {
            string ans="";
            for(int i=0;i<n;i++)
                ans+='9';
            return ans;
        }
        if(n<=4)
        {
            string ans="";
           if(n==1){
               for(int i=1;i<=9;i++)
               {
                   if(i%k==0){
                       string temp = to_string(i);
                       if(ispali(temp))
                       {
                           ans=temp;
                       }
                   }
               }
           }
           if(n==2){
               for(int i=10;i<=99;i++)
               {
                   if(i%k==0){
                       string temp = to_string(i);
                       if(ispali(temp))
                       {
                           ans=temp;
                       }
                   }
               }
           }
           if(n==3){
               for(int i=100;i<=999;i++)
               {
                   if(i%k==0){
                       string temp = to_string(i);
                       if(ispali(temp))
                       {
                           ans=temp;
                       }
                   }
               }
           }
            if(n==4){
               for(int i=1000;i<=9999;i++)
               {
                   if(i%k==0){
                       string temp = to_string(i);
                       if(ispali(temp))
                       {
                           ans=temp;
                       }
                   }
               }
           }
            return ans;
        }
        if(k==2)
        {
            string ans="";
            for(int i=0;i<n;i++)
                ans+="9";
            ans[0]='8';
            ans[n-1]='8';
            return ans;
        }
        if(k==4)
        {
            string ans="";
            for(int i=0;i<n;i++)
                ans+="9";
            ans[0]='8',ans[1]='8';
            ans[n-1]='8',ans[n-2]='8';
            return ans;
        }
        if(k==8)
        {
            string ans="";
            for(int i=0;i<n;i++)
                ans+="9";
            ans[0]='8',ans[1]='8',ans[2]='8';
            ans[n-1]='8',ans[n-2]='8',ans[n-3]='8';
            return ans;
        }
        if(k==5)
        {
            string ans="";
            for(int i=0;i<n;i++)
                ans+="9";
            ans[0]='5';
            ans[n-1]='5';
            return ans;
        }
        if(k==6)
        {
            int sum=0;
            string ans="";
            for(int i=0;i<n;i++)
                ans+="9",sum+=9;
            ans[0]='8';
            ans[n-1]='8';
            sum-=2;
            if((n%2)==0)
            {

                for(int i=9;i>=0;i--)
                {
                    if((sum+2*i)%3==0)
                    {
                        ans[n/2]='0'+i;
                        ans[n/2-1]='0'+i;
                            break;
                    }
                }
            }
            if((n%2)!=0)
            {
                 for(int i=9;i>=0;i--)
                {
                    if((sum+i)%3==0)
                    {
                        ans[n/2]='0'+i;
                            break;
                    }
                }
            }
            return ans;
        }
         if(k==7)
        {
            string ans="";
             if(n==5)
                 return "99799";
             if(n==6)
                 return "999999";
            for(int i=0;i<n;i++)
                ans+='9';
            string subs="";
