                subs+='9';
            subs[n-2]='1';
            subs[n-3]='8';
            if(n%2==0)
            {

                for(int i=9;i>=0;i--)
                {
                    string temp=subs;
                    temp[n/2]='0'+i;
                    temp[n/2-1]='0'+i;
                    if(Remn(temp,7)==0)
                    {
                        ans[n/2]='0'+i;
                        ans[n/2-1]='0'+i;
                        break;
                    }
                }
            }
