            {
                for(int i=9;i>=0;i--)
                {
                    string temp=subs;
                    temp[n/2]='0'+i;
                    if(Remn(temp,7)==0)
                    {
                        ans[n/2]='0'+i;
                        break;
                    }
                }
            }

            return ans;
        }
        return "";
    }
