class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n == 1:
            for i in range(9, 4, -1):
                if i % k == 0:
                    return str(i)
        if n == 2:
            for i in range(9, 4, -1):
                if i * 11 % k == 0:
                    return str(i * 11)
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        if k == 2:
            return '8' + ('9' * (n - 2)) + '8'
        if k == 4:
            if n < 5:
                return '8' * n
            return '88' + ('9' * (n - 4)) + '88'
        if k == 5:
            return '5' + ('9' * (n - 2)) + '5'
        if k == 6:
            if n % 2 == 0:
                return '8' + ('9' * ((n - 4) // 2)) + '77' + ('9' * ((n - 4) // 2)) + '8'
            return '8' + ('9' * ((n - 3) // 2)) + '8' + ('9' * ((n - 3) // 2)) + '8'
        if k == 7:
            if n % 2 == 0:
                ans = '9' * ((n - 2) // 2) + '00' + '9' * ((n - 2) // 2)
                tmp = 0
                for i in range(n - 1, 4, -6):
                    tmp += int(ans[i - 5:i + 1])
                if n % 6 != 0:
                    tmp += int(ans[:n % 6])
                base = pow(10, ((n - 2) // 2), 7)
                for i in range(9, 0, -1):
                    if (i * 11 * base + tmp) % 7 == 0:
                        return '9' * ((n - 2) // 2) + str(i) * 2 + '9' * ((n - 2) // 2)
            ans = '9' * ((n - 1) // 2) + '0' + '9' * ((n - 1) // 2)
            tmp = 0
            for i in range(n - 1, 4, -6):
                tmp += int(ans[i - 5:i + 1])
            if n % 6 != 0:
                tmp += int(ans[:n % 6])
            base = pow(10, ((n - 1) // 2), 7)
            for i in range(9, 0, -1):
                if (i * base + tmp) % 7 == 0:
                    return '9' * ((n - 1) // 2) + str(i) + '9' * ((n - 1) // 2)
        if n < 7:
            return '8' * n
        return '888' + '9' * (n - 6) + '888'
