class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s;
        if(k==1||k==3||k==9){
            for(int i=0; i<n; i++){
                s.push_back('0'+9);
            }
            return s;
        }
        if(k==5){
            s.push_back('5');
            for(int i=1; i<n-1; i++){
                s.push_back('9');
            }
            if(n>1)s.push_back('5');
            return s;
        }
        if(k==2){
            s.push_back('8');
            for(int i=1; i<n-1; i++){
                s.push_back('9');
            }
            if(n>1)s.push_back('8');
            return s;
        }
        if(k==4||k==6){
            if(n==1&&k==4)return "8";
            if(n==1&&k==6)return "6";
            if(n==2){
                if(k==4)return "88";
                else return "66";
            }
            s.push_back('8');
            for(int i=1; i<n-1;i++){
                s.push_back('9');
            }
            s.push_back('8');
            // cout<<s<<"\n";
            if(k==6&&n%2){
                s[n/2]='8';
            }
            else if(k==6){
                s[(n-1)/2]='7';
                s[n/2]='7';
            }
            else if(k==4){
                s[1]='8';
                s[n-2]='8';
            }
            // cout<<s<<"\n";
            return s;
        }
        if(k==8){
            if(n<=6){
                for(int i=0; i<n; i++)s.push_back('8');
                return s;
            }
            s.append("888");
            for(int i=3; i<n-3;i++)s.push_back('9');
            s.append("888");
            return s;
        }
        if(k==7){
            if(n==1)return "7";
            if(n==2)return "77";
            if(n==3)return "959";
            if(n==4) return "9779";
            if(n==5)return "99799";
            if(n==6)return "999999";
            for(int i=0; i<n; i++)s.push_back('9');
            switch((n-4)%12){
                case 0: s[(n-1)/2]='7';s[(n)/2]='7';break;
                case 1: s[(n-1)/2]='7';break;
                case 2: break;
                case 3: s[(n-1)/2]='4';break;
                case 4: s[(n-1)/2]='4';s[(n)/2]='4';break;
                case 5: s[(n-1)/2]='6';break;
                case 6: s[(n-1)/2]='4';s[(n)/2]='4';break;
                case 7: s[(n-1)/2]='4';break;
                case 8: break;
                case 9: s[(n-1)/2]='7';break;
                case 10: s[(n-1)/2]='7';s[(n)/2]='7';break;
                case 11: s[(n-1)/2]='5';break;

            }
            return s;
        }
        return s;
    }
};
