class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            string res(n, '9');
            return res;
        }
        if (k == 2) {
            string res(n, '9');
            res[n-1] = '8';
            res[0] = '8';
            return res;
        }
        if (k == 4) {
            if (n <= 1) return "8";
            string res(n, '9');
            res[n-1] = '8';
            res[n-2] = '8';
            res[0] = '8';
            res[1] = '8';
            return res;
        }
        if (k == 5) {
            string res(n, '9');
            res[n-1] = '5';
            res[0] = '5';
            return res;
        }
        if (k == 6) {
            if (n <= 1) return "6";
            if (n <= 2) return "66";

            string res(n, '9');
            res[n-1] = '8';
            res[0] = '8';

            if (n % 2 == 0) {
                res[n/2] = '7';
                res[n/2 - 1] = '7';
            }
            else {
                res[n/2] = '8';
            }

            return res;
        }
        if (k == 7) {
            // if (n <= 1) return "7";
            // if (n <= 2) return "77";
            // if (n <= 3) return "959";
            // if (n <= 4) return "9779";
            // if (n <= 5) return "99799";
            // if (n == 7) return "9994999";
            // if (n == 8) return "99944999";
            // if (n == 9) return "999969999";
            // if (n == 10) return "9999449999";
            // if (n == 11) return "99999499999";
            // if (n == 12) return "999999999999";
            // if (n == 13) return "9999997999999";
            // if (n == 14) return "99999977999999";
            string res(n, '9');

            if (n % 12 == 1 || n % 12 == 5) {
                res[n/2] = '7';
            }
            else if (n % 12 == 2 || n % 12 == 4) {
                res[n/2] = '7';
                res[n/2 - 1] = '7';
            }
            else if (n % 12 == 3) {
                res[n/2] = '5';
            }
            else if (n % 12 == 7 || n % 12 == 11) {
                res[n/2] = '4';
            }
            else if (n % 12 == 8 || n % 12 == 10) {
                res[n/2] = '4';
                res[n/2 - 1] = '4';
            }
            else if (n % 12 == 9) {
                res[n/2] = '6';
            }

            return res;
        }
        if (k == 8) {
            string res(n, '9');



            res[n-1] = '8';
            if (n >= 2) res[n-2] = '8';
            if (n >= 3) res[n-3] = '8';
            res[0] = '8';
            if (n >= 2) res[1] = '8';
            if (n >= 3) res[2] = '8';

            return res;
        }

        return "";
    }
};
