class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 7) {
            // Function to compute the modulo of large palindromic strings
            auto isDivisibleBy7 = [](const string& s) -> bool {
                long long mod = 0;
                long long factor = 1;
                for (int i = s.size() - 1; i >= 0; --i) {
                    mod = (mod + (s[i] - '0') * factor) % 7;
                    factor = (factor * 10) % 7;
                }
                return mod == 0;
            };

            string result(n, '9');
            if (n % 2 == 1) { // Odd length
                for (int mid = 9; mid >= 0; --mid) {
                    result[n / 2] = '0' + mid;
                    if (isDivisibleBy7(result)) {
                        return result;
                    }
                }
            } else { // Even length
                for (int mid = 9; mid >= 0; --mid) {
                    result[n / 2 - 1] = result[n / 2] = '0' + mid;
                    if (isDivisibleBy7(result)) {
                        return result;
                    }
                }
            }
        }

        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        }

        string result(n, '9');

        if (k == 2) {
            if (n == 1) return "8";
            result[0] = result[n - 1] = '8';
        } else if (k == 5) {
            if (n == 1) return "5";
            result[0] = result[n - 1] = '5';
        } else if (k == 6) {
            if (n == 1) return "6";
            if (n == 2) return "66";
            result[0] = result[n - 1] = '8';
            if (n % 2 == 1) {
                result[n / 2] = '8';
            } else {
                result[(n - 2) / 2] = result[n / 2] = '7';
            }
        } else if (k == 4) {
            if (n == 1) return "8";
            result[0] = result[1] = result[n - 2] = result[n - 1] = '8';
        } else if (k == 8) {
            if (n == 1) return "8";
            if (n == 2) return "88";
            if (n == 3) return "888";
            result[0] = result[1] = result[2] = result[n - 3] = result[n - 2] = result[n - 1] = '8';
        } else {
            return "-1";
        }

        return result;
    }
};
