class Solution {
public:
    string largestPalindrome(int n, int k) {
        string res(n,'9');
        auto test =[&](){
            int re=0;
            for(char ch:res) re=(re*10+ch-'0')%k;
            return re==0;
        };
        if(k==1 || k==3 || k==9){
            return res;
        } else if(k==5){
            res[0]=res.back()='5';
            return res;
        } else if(k==7){
            if(n<=2) return string(n,'7');
            for(char ch='9';ch>='0';ch--){
                    if(n%2){
                        res[n/2]=ch;
                    } else {
                        res[n/2]=res[n/2-1]=ch;
                    }
                    // cout << res << '\n';
                    if(test()) break;
            }
            return res;
        } else if(k==6){
            if(n==1) return "6";
            else if(n==2) return "66";
            res[0]=res.back()='8';
            if(n>2){
                for(char ch='9';ch>='0';ch--){
                    if(n%2){
                        res[n/2]=ch;
                    } else {
                        res[n/2]=res[n/2-1]=ch;
                    }
                    if(test()) break;
                }
            }
            return res;
        } else {
            for(int i=0;k%2==0;k/=2,i++){
                if(n>=i+1) res[i]=res[n-1-i]='8';
            }
            return res;
        }
    }
};
