class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans(n,'9');
        if(k==1||k==3||k==9)return ans;  
        if(k==2)
        {
            ans[0]='8';
            ans[n-1]='8';
        }
        if(k==4)
        {
            ans[0]='8';
            ans[n-1]='8';
            if(n>2)
            {
                ans[1]='8';
                ans[n-2]='8';
            }
        }
        if(k==8)
        {
            ans[0]='8';
            ans[n-1]='8';
            if(n>2)
            {
                ans[1]='8';
                ans[n-2]='8';
            }
            if(n>4)
            {
                ans[2]='8';
                ans[n-3]='8';
            }
        }
        if(k==5)
        {
            ans[0]='5';
            ans[n-1]='5';
        }
        if(k==6)
        {
            if(n<=2)return string(n,'6');
            ans[0]='8';
            ans[n-1]='8';
            if(n&1)
            {
                ans[n/2]='8';
            }
            else
            {
                ans[n/2]='7';
                ans[n/2-1]='7';
            }
        }
        if(k==7)
        {
            char subs[]="977577944644";
            int p=n%12;
            if(n&1)
            {
                ans[n/2]=subs[p];
            }
            else
            {
                ans[n/2]=subs[p];
                ans[n/2-1]=subs[p];
            }
        }
        return ans;
    }
};
