class Solution {
public:
    bool isdiv(string& s,int k){
        int cur = 0;
        for(char ch: s){
            cur = (cur*10+ch-'0')%k;
        }
        return cur==0;
    }
    string largestPalindrome(int n, int k) {
        string ans(n,'9');
        switch(k){
            case 0:
                ans[0] = '0';break;
            case 2:
                ans[0] = '8';break;
            case 4:
                ans[0] = '8';
                ans[1] = '8';
                break;
            case 6:
                ans[0] = '8';break;
            case 8:
                ans[0] = '8';
                ans[1] = '8';
                ans[2] = '8';
                break;
            case 5:
                ans[0] = '5';break;
        }
        for(int i = 0 ;i<min(n,3);i++){
            ans[n-i-1] = ans[i];
        }
        int mid = (n-1)/2;
        while(!isdiv(ans,k)){
            for(int i = mid;i>=0;i--){
                if(ans[i]>'0'){
                    ans[i] = ans[n-i-1] = ans[i]-1;
                    break;
                }
                else{
                    ans[i] = ans[n-i-1] = '9';
                }
            }
        }
        return ans;
    }
};
