class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(100000)
        match k:
            case 1 | 3 | 9:
                return '9' * n
            case 2:
                if n < 2:
                    return '8'
                return '8' + '9' * (n - 2) + '8'
            case 4:
                if n < 4:
                    return '8' * n
                return '88' + '9' * (n - 4) + '88'
            case 5:
                if n < 2:
                    return '5'
                return '5' + '9' * (n - 2) + '5'
            case 6:
                if n < 2:
                    return '6'
                mid = n - 2
                half = mid >> 1
                left = '8' + '9' * (half)
                right = left[::-1]
                if (half + 1) << 1 != n:
                    for i in range(9, -1, -1):
                        ans = f'{left}{i}{right}'
                        if int(ans) % 6 == 0:
                            return ans
                for i in range(9, -1, -1):
                    ans = f'{left[:-1]}{i}{i}{right[1:]}'
                    if int(ans) %6 == 0:
                        return ans
            case 7:
                mid = n >> 1
                left = right = '9' * mid
                if mid << 1 != n:
                    for i in range(9, -1, -1):
                        ans = f'{left}{i}{right}'
                        if int(ans) % 7 == 0:
                            return ans
                for i in range(9, -1, -1):
                    ans = f'{left[:-1]}{i}{i}{right[1:]}'
                    if int(ans) % 7 == 0:
                        return ans
            case 8:
                if n < 6:
                    return '8' * n
                return '888' + '9' * (n - 6) + '888'
            case _:
                return 0
