#define pb push_back

class Solution {
public:
    string largestPalindrome(int n, int k) {
       string ans="";

        function<bool()> kk = [&]() {
            long long r = 0;
            for (auto c:ans) {
                r = (r*10 + c-'0')%7;
            }
            return (r==0);
        };
        for(int i=0; i<n; ++i) ans.pb('9');
        switch (k){
            case 2:
                ans[n-1] = '8';
                ans[0] = '8';
                break;
            case 3:
                break;
            case 4:
                ans[0] = '8';
                ans[n-1] = '8';
                if(n>2) {
                    ans[1] ='8';
                    ans[n-2]='8';
                }
                break;
            case 5:
                ans[0] = '5';
                ans[n-1] = '5';
                break;
            case 6:
                if(n<3) {
                    ans.clear();
                    ans.pb('6');
                    if(n==2) ans.pb('6');
                    break;
                }else if(n%2) {
                    ans[0] = '8';
                    ans[n-1] = '8';
                    ans[n/2] = '8';
                }else {
                    ans[0] = '8';
                    ans[n-1] ='8';
                    ans[n/2-1]='7';
                    ans[n/2] ='7';
                }
                break;
            case 7:
                if(kk()) {
                    break;
                }
                char tmp;
                for(int i=0; i<9; ++i) {
                    tmp = '8'-i;
                    if(n%2) {
                        ans[n/2] = tmp;
                        if(kk()) break;
                    }else {
                        ans[n/2-1] = tmp;
                        ans[n/2] = tmp;
                        if(kk()) break;
                    }
                }
                break;
            case 8:
                if(n==1) {
                    ans = "8";
                    break;
                }else if(n==2) {
                    ans = "88";
                    break;
                }else {
                    for(int i=0; i<3; ++i) {
                        ans[i] = '8';
                        ans[n-1-i] = '8';
                    }
                }
                break;
            default :
                break;

        }

        return ans;

    }
};
