class Solution {
public:
    string largestPalindrome(int k, int n) {
        if(n==1||n==3||n==9){
            return string(k,'9');
        }else if(n==2){
            if(k==1)return "8";
            return string("8")+string(k-2,'9')+string("8");
        }else if(n==4){
            if(k<4)return string(k,'8');
            return string(2,'8')+string(k-4,'9')+string(2,'8');
        }else if(n==5){
            if(k==1)return "5";
            return string("5")+string(k-2,'9')+string("5");
        }else if(n==6){
            if(k<3)return string(k,'6');
            if(k%2)return string("8")+string((k-3)/2,'9')+string("8")+string((k-3)/2,'9')+string("8");
            return string("8")+string((k-4)/2,'9')+string("77")+string((k-4)/2,'9')+string("8");
        }else if(n==7){
            if(k<3)return string(k,'7');
            int a=(k-3)%12;
            if(a==11)return string((k-2)/2,'9')+string("77")+string((k-2)/2,'9');
            if(a==10)return string((k-1)/2,'9')+string("7")+string((k-1)/2,'9');
            if(a==9)return string(k,'9');
            if(a==8)return string((k-1)/2,'9')+string("4")+string((k-1)/2,'9');
            if(a==7)return string((k-2)/2,'9')+string("44")+string((k-2)/2,'9');
            if(a==6)return string((k-1)/2,'9')+string("6")+string((k-1)/2,'9');
            if(a==5)return string((k-2)/2,'9')+string("44")+string((k-2)/2,'9');
            if(a==4)return string((k-1)/2,'9')+string("4")+string((k-1)/2,'9');
            if(a==3)return string(k,'9');
            if(a==2)return string((k-1)/2,'9')+string("7")+string((k-1)/2,'9');
            if(a==1)return string((k-2)/2,'9')+string("77")+string((k-2)/2,'9');
            return string((k-1)/2,'9')+string("5")+string((k-1)/2,'9');
        }else if(n==8){
            if(k<6)return string(k,'8');
            return string(3,'8')+string(k-6,'9')+string(3,'8');
        }
        return "";
    }
};
