class Solution {
    public String largestPalindrome(int n, int k) {
        StringBuilder str = new StringBuilder();
        if (k == 1 || k == 3 || k == 9){
            for(int i = 0; i < n; i++) str.append("9");
        }

        if (k == 2){
            str.append("8");
            for(int i = 0; i < n-2; i++) str.append("9");
            if (n > 1) str.append("8");
        }

        if (k == 5){
            str.append("5");
            for(int i = 0; i < n-2; i++) str.append("9");
            if (n > 1) str.append("5");
        }

        if (k == 4){
            if (n <= 4) {
                for(int i = 0; i < n; i++) str.append("8");
            }
            else{
                str.append("88");

                for(int i = 0; i < n-4; i++) str.append("9");

                str.append("88");
            }
        }

        if (k==8){
            if (n <= 6){
                for(int i = 0; i < n; i++) str.append("8");
            }
            else{
                str.append("888");

                for(int i = 0; i < n-6; i++) str.append("9");

                str.append("888");
            }
        }

        if (k == 6) { 
            if (n == 1) str.append("6");
            else if (n == 2) str.append("66");

            else {
                str.append("8");

                int count = n-2;

                if (count %2 == 0){
                    int ext = count/2 - 1;

                    for(int i = 0; i < ext; i++) str.append("9");
                    str.append("77");
                    for(int i = 0; i < ext; i++) str.append("9");
                }
                else{
                    int ser = count/2;

                    for(int i = 0; i < ser; i++) str.append("9");
                    str.append("8");
                    for(int i = 0; i < ser; i++) str.append("9");
                }

                str.append("8");
            }
        }

        if (k == 7){

            String arr[] = {"7", "77", "5", "77", "7", "99", "4", "44", "6", "44", "4", "99"} ;

            String mid = arr[(n-1)%12];

            int ext = n-mid.length();

            for(int i = 0; i < ext/2; i++){
                str.append("9");
            }
            str.append(mid);
            for(int i = 0; i < ext/2; i++){
                str.append("9");
            }

        }

        return str.toString();
    }

}
