class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return n * "9"
        if k == 2:
            if n == 1:
                return "8"
            return "8" + (n - 2) * "9" + "8"
        if k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "88" + (n-4) * "9" + "88"
        if k == 5:
            if n <= 2:
                return "5" * n
            else:
                return "5" + "9" * (n-2) + "5"
        if k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                return "66"
            elif n == 3:
                return "888"
            elif n == 4:
                return "8778"
            elif n == 5:
                return "89898"
            elif n & 1 == 0:
                return "8" + "9" * (n // 2 - 2) +"77" + "9" * (n // 2 - 2) + "8"
            else:
                return "8" + "9" * (n // 2 - 1) +"8" + "9" * (n // 2 - 1) + "8"
        if k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "888" + (n -6) * "9" + "888"
        if k == 7:
            if n % 12 in (0,6):
                return "9" * n
            elif n % 12 in (1,2,4,5):
                return ((n-1) // 2) * "9" + (1 if (n & 1) else 2) * "7" + ((n-1) // 2) * "9" 
            elif n % 12 == 3:
                return ((n-1) // 2) * "9" + (1 if (n & 1) else 2) * "5" + ((n-1) // 2) * "9"
            elif n % 12 in (7,8,10,11):
                return ((n-1) // 2) * "9" + (1 if (n & 1) else 2) * "4" + ((n-1) // 2) * "9"
            elif n % 12 == 9:
                return ((n-1) // 2) * "9" + (1 if (n & 1) else 2) * "6" + ((n-1) // 2) * "9"
