class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(0)
        """
        // 1: 9999999
        // 2: 8999998
        // 3: same as 1 (old: if digits added up are divisible by 3, which means either 99999 or 99899 or 99799)
        // 4: last two digits are divisible by 4, so 8899999988
        // 5: 5999995
        // 6: div by both 2 and 3 (basically div by 3 but even), 8998998 if n is odd, else 89977998
        // 7: no good pattern, so try 9999999, 9998999, ..., 9989899, ...
        // 8: ends in 888, so 8889999888
        // 9: same as 1 (old: sum of digits are divisible by 9, which means 99999, 99899, ..., or 99199)
        """
        if k == 1 or k == 3 or k == 9:
            return str(10**n - 1)
        elif k == 2:
            if n > 1:
                return str(10**n - 2 - 10**(n - 1))
            else:
                return "8"
        elif k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "88" + str(10**(n - 4) - 1) + "88"
        elif k == 5:
            if n <= 2:
                return "5" * n
            else:
                return "5" + str(10**(n - 2) - 1) + "5"
        elif k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                return "66"
            elif n == 3:
                return "888"
            elif n == 4:
                return "8778"
            elif n % 2 == 1:
                return str(10**n - 2 - 10**(n - 1) - 10**(n // 2))
            else:
                return str(10**n - 2 - 10**(n - 1) - 22*(10**(n // 2 - 1)))
        elif k == 7:
            a = 10**n - 1
            if n % 2 == 1:
                while a % 7 != 0:
                    a -= 10**(n // 2)
            else:
                while a % 7 != 0:
                    a -= 10**(n // 2)
                    a -= 10**(n // 2 - 1)
            return str(a)
        elif k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "888" + str(10**(n - 6) - 1) + "888"



        return "-1"
