import sys
sys.set_int_max_str_digits(10**6)
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k==1 or k==3 or k==9:
            return "9"*n

        if k==2:
            if n==1:
                return "8"
            return "8"+"9"*(n-2)+"8"
        if k==4:
            s = "8"*min(4,n)            
            if n<=4:
                return s
            return s[0:2] + "9"*(n-4) + s[2:]
        if k==8:
            s = "8"*min(6,n)            
            if n<=6:
                return s
            return s[0:3] + "9"*(n-6) + s[3:]
        if k==5:
            return "5" if n==1 else "5"+"9"*(n-2)+"5"
        if n<6:
            if n==1:
                return str(k)
            ans = 0
            for i in reversed(range(int(10**((n//2)-1)),int(10**(n//2)))):
                if n%2==0:
                    s = str(int( str(i)[::-1] + str(i) ))
                    if s==s[::-1] and int(s)%k==0:
                        ans = max(int(s),ans)
                else:
                    for d in range(10):
                        s = str(int(str(i)[::-1] + str(d) + str(i)))
                        if s==s[::-1] and int(s)%k==0:
                            ans = max(ans,int(s))
            return str(ans)
        if k==7:
            for i in range(9,-1,-1):
                mid = str(i)*(2 if n%2==0 else 1)
                other = "9"*((n-1)//2)
                ans = other + mid + other
                if int(ans)%7==0:
                    return ans
            return "0"
        # 6
        if n%2==0:
            return "8" + ((n-4)//2)*"9" + "77" + ((n-4)//2)*"9" + "8"
        else:
            return "8" + ((n-3)//2)*"9" + "8" + ((n-3)//2)*"9" + "8"
