class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k==1:return '9'*n
        if k==2:
            if n<=2:return '8'*n
            else:return '8'+'9'*(n-2)+'8'
        if k==3:return '9'*n
        if k==4:
            if n<=4:return '8'*n
            else:return '88'+'9'*(n-4)+'88'
        if k==5:
            if n<=2:return '5'*n
            else:return '5'+'9'*(n-2)+'5'
        if k==6:
            if n<=2:return '6'*n
            if n%2:
                return '8'+'9'*(n//2-1)+'8'+'9'*(n//2-1)+'8'
            else:
                return '8'+'9'*(n//2-2)+'77'+'9'*(n//2-2)+'8'
        if k==7:
            if n<=2:return '7'*n
            temp=['5','77','979','9999','99499','994499','9996999']
            temp+=list('9'*i+temp[-i-1]+'9'*i for i in range(1,6))
            return '9'*((n-3)//12*6+1)+temp[(n+9)%12]+'9'*((n-3)//12*6+1)
        if k==8:
            if n<=6:return '8'*n
            else:return '888'+'9'*(n-6)+'888'
        if k==9:return '9'*n
