class Solution {
public:
    vector<long long> countKConstraintSubstrings(string s, int k, vector<vector<int>>& q) {
        int  m = q.size();

        vector<long long> ans;
        int n = s.size();
        vector<long long> dp(n+1,0);
        int j=0;
        long long cz=0,co = 0;
        vector<int> mrange(n,0);
        for(int i=0;i<n;i++){
            if(s[i]=='0')cz++;
            else co++;
            while(cz>k&&co>k){
                if(s[j]=='0')cz--;
                else co--;
                j++;
            }
            mrange[i] = j;/// iclusive
            dp[i+1] = dp[i]+i-j+1;
        }
        //cout<<dp[n]<<endl;
        for(auto it:q){
            int u = it[0], v= it[1];
            long long len = v-u+1;
            if(mrange[v]<=u)ans.push_back(len*(len+1)/2);
            else{
                auto it = upper_bound(mrange.begin(),mrange.end(), u)-mrange.begin()-1;
                len = it-u+1;
                ans.push_back(dp[v+1]-dp[it+1]+(len*(len+1)/2));
            }
        }
        return ans;
    }
};
