class Solution {
    public long[] countKConstraintSubstrings(String s, int k, int[][] queries) {
        int n=s.length();
       int[] a=new int[n];
        Arrays.fill(a,n-1);
        int p=0,q=0,pr=0;
        for(int i=0;i<n;i++){
            if(s.charAt(i)=='0')p++;
            else q++;
            while(p<n&&p>k&&q>k){
                a[pr]=i-1;
                if(s.charAt(pr)=='0')p--;
                else q--;
                pr++;

            }

        }
        // for(int i=0;i<n;i++){
        //     System.out.print(a[i]+" ");
        // }
        // System.out.println("");
        long[] b=new long[n];
        for(int i=0;i<n;i++){
            long t=(long)(a[i]-i+1);
            b[i]=t;
            if(i>0)b[i]+=b[i-1];
        }


         q=queries.length;
        long[] ans=new long[q];
        int ind=0;
        for(int[] row:queries){
            int l=row[0],r=row[1],x=r+1;
            long cost=0;
            while(l<=r){
                int mid=(l+r)/2;
                if(a[mid]>row[1]){
                    x=Math.min(x,mid);
                    r=mid-1;
                }
                else l=mid+1;
            }
            if(x<=row[1]){
                long t=row[1]-x+1;
                cost=(t*(t+1))/2;
            }
            if(x-1>=row[0]){
               long t=b[x-1];
                if(row[0]-1>=0)t-=b[row[0]-1];
                cost+=t;
            }

            ans[ind]=cost;
            ind++;
        }
        return ans;
    }
}
