function countKConstraintSubstrings(s: string, k: number, queries: number[][]): number[] {
    let len = s.length, count: number[] = new Array(len).fill(0);
    let startIdx = new Array(len).fill(0);
    let i = 0, zero = 0, one = 0;
    for(let j = 0; j < len; j++) {
        s[j] === '0' ? zero++ : one++;
        while(zero > k && one > k) {
            s[i++] === '0' ? zero-- : one--;
        }
        count[j] = (count[j-1] || 0) + j - i + 1;
        startIdx[j] = i;
    }

    let ans: number[] = new Array(queries.length).fill(0);
    for(let i = 0; i < queries.length; i++) {
        let [x, y] = queries[i]
        ans[i] = count[y] - (count[x-1] || 0);
        let k = x;
        while(k <= y && startIdx[k] < x) {
            ans[i] -= x - startIdx[k++];
        }
    }
    return ans;
};
