class Solution {
public:
    vector<long long> countKConstraintSubstrings(string s, int k, vector<vector<int>>& queries) 
    {
        long long n = s.size(),l,r,o=0,z=0,cur,i,len;
        vector <long long> ans,pre(n+1),a(n);
        l = 0;
        for(r=0;r<n;r++)
        {
            if(s[r] == '1')
            {
                o++;
            }
            else
            {
                z++;
            }
            while(z > k && o > k)
            {
                if(s[l] == '1')
                {
                    o--;
                }
                else
                {
                    z--;
                }
                l++;
            }
            cur = r - l + 1;
            a[r] = cur;
        }
        for(i=0;i<n;i++)
        {
            pre[i+1] = pre[i] + a[i];
        }
        for(auto &q : queries)
        {
            l = q[0];
            r = q[1];
            cur = 0;
            len = 0;
            for(i=l;i<=r;i++)
            {
                len++;
                if(a[i] <= len)
                {
                    cur = cur + pre[r+1] - pre[i];
                    break;
                }
                else
                {
                    cur = cur + len;
                }
            }
            ans.push_back(cur);
        }
        return ans;
    }
};
