class Solution {
public:
    vector<long long> countKConstraintSubstrings(string s, int k, vector<vector<int>>& queries) {

        int n=s.length();
        long long ans[n+1];
        for(int i=0;i<=n;i++){
            ans[i]=0;
        }
        int k1=0;
        long long zero=0,one=0;
        int last[n];
        for(int i=0;i<n;i++){
            last[i]=0;
        }
        for(int i=0;i<n;i++){
            if(s[i]=='0'){
                zero++;
            }
            else{
                one++;
            }
            if(one>k && zero>k){
                for(int j=k1;j<i;j++){
                    if(s[j]=='0'){
                        zero--;
                    }
                    else{
                        one--;
                    }
                    if(zero<=k || one<=k){
                        k1=j+1;
                        break;
                    }
                }
            }
            last[i]=k1;
            ans[i+1]=ans[i]+zero+one;
        }
        long long last1[n+1];
        for(int i=0;i<=n;i++){
            last1[i]=0;
        }
        for(int i=1;i<=n;i++){
            last1[i]=last[i-1]+last1[i-1];
        }
        vector<long long> ansvec;
        for(int i=0;i<queries.size();i++){
            int l=queries[i][0],r=queries[i][1];
            long long v=ans[r+1]-ans[l];
            int z=lower_bound(last+l,last+r+1,l)-last;
            int sum=last1[z]-last1[l]-(z-l)*(l);
            cout<<sum<<endl;
            ansvec.push_back(v+sum);
        }
        return ansvec;
    }
};
