class Solution {
public:
    vector<long long> countKConstraintSubstrings(string s, int k, vector<vector<int>>& queries) {
        vector<long long> v(s.length());
        vector<long long> pf(s.length()+1);
        pf[0] = 0;
        int cnt[2] = {0};
        int l = 0, r = 0;
        for(; r < s.length(); r++) {
            cnt[s[r]-'0']++;
            while(cnt[0] > k && cnt[1] > k) {
                cnt[s[l]-'0']--;
                l++;
            }
            v[r] = r - l + 1;
            pf[r+1] = pf[r] + v[r];
        }
        vector<long long> res;
        for(vector<int>& q: queries) {
            int l = q[0], r = q[1], m;
            while(l <= r) {
                m = (l + r) >> 1;
                if(v[m] <= m - q[0] + 1) r = m - 1;
                else l = m + 1;
            }
            long long x = l - q[0];
            x = x * (x+1) >> 1;
            x += pf[q[1]+1] - pf[l];
            res.push_back(x);
        }
        return res;
    }
};
