class Solution 
{
public:
    vector<long long> countKConstraintSubstrings(string s, int k, vector<vector<int>>& queries) 
    {
        int n = s.length(), one = 0, zero = 0, l = 0;
        vector<long long> left(n), prefix(n);
        vector<long long> ans;
        for(int r = 0; r < n; r++)
        {
            if(s[r] == '1')
            {
                one++;
            }
            else
            {
                zero++;
            }
            while(one > k && zero > k)
            {
                if(s[l] == '1')
                {
                    one--;
                }
                else 
                {
                    zero--;
                }
                l++;
            }
            left[r] = l;
            prefix[r] = r > 0 ? prefix[r - 1] + r - l + 1 : r - l + 1;
        }
        prefix.insert(prefix.begin(), 0);
        for(auto& it : queries)
        {
            ans.push_back(prefix[it[1] + 1] - prefix[it[0]]);
            for(int i = it[0]; i <= it[1]; i++)
            {
                if(left[i] < it[0])
                {
                    ans.back() -= it[0] - left[i];
                }
                else
                {
                    break;
                }
            }
        }
        return ans;
    }
};
