class Solution {
public:
    int solve(vector<vector<int>>&points,int ind){
         int maxSum=INT_MIN,minSum=INT_MAX;
        int maxSub = INT_MIN,minSub=INT_MAX;
        for(int i=0;i<points.size();i++){
            if(i==ind)continue;
            if(points[i][0]+points[i][1]>maxSum){
                maxSum = points[i][0]+points[i][1];
            }
            if(points[i][0]+points[i][1]<minSum){
                minSum = points[i][0]+points[i][1];
            }
            if(points[i][0]-points[i][1]>maxSub){
                maxSub = points[i][0]-points[i][1];
            }
            if(points[i][0]-points[i][1]<minSub){
                minSub = points[i][0]-points[i][1];
            }
        }
        return max(maxSum-minSum,maxSub-minSub);
    }
    int minimumDistance(vector<vector<int>>& points) {
        int maxSum=INT_MIN,minSum=INT_MAX;
        int maxSumi=0,minSumi=0;
        int maxSub = INT_MIN,minSub=INT_MAX;
        int maxSubi = 0,minSubi =0;
        for(int i=0;i<points.size();i++){
            if(points[i][0]+points[i][1]>maxSum){
                maxSum = points[i][0]+points[i][1];
                maxSumi = i;
            }
            if(points[i][0]+points[i][1]<minSum){
                minSum = points[i][0]+points[i][1];
                minSumi = i;
            }
            if(points[i][0]-points[i][1]>maxSub){
                maxSub = points[i][0]-points[i][1];
                maxSubi = i;
            }
            if(points[i][0]-points[i][1]<minSub){
                minSub = points[i][0]-points[i][1];
                minSubi = i;
            }
        }
        int ans = solve(points,maxSumi);
        ans = min(ans,solve(points,minSumi));
        ans = min(ans,solve(points,maxSubi));
        ans = min(ans,solve(points,minSubi));
        return ans;
    }
};
