class Solution {
public:

    int minimumDistance(vector<vector<int>>& p) {
        int n=p.size();

       vector<pair<int,int>>v1(n),v2(n);

        for(int i=0;i<n;i++){
            v1[i]={p[i][0]+p[i][1],i};
             v2[i]={p[i][0]-p[i][1],i};
        }

        sort(v1.begin(),v1.end());
        sort(v2.begin(),v2.end());

        int ans=max(v1.back().first - v1.front().first, v2.back().first - v2.front().first);

        int i1=v1.back().second,i2=v1.front().second,i3=v2.back().second,i4=v2.front().second;
        int i=0,j=n-1;
        while(v2[i].second==i1) i++;
        while(v2[j].second==i1) j--;

         int ans1=max(v1[n-2].first-v1.front().first,v2[j].first-v2[i].first);
        ans=min(ans,ans1);

        i=0;j=n-1;

         while(v2[i].second==i2) i++;
        while(v2[j].second==i2) j--;

         ans1=max(v1[n-1].first-v1[1].first,v2[j].first-v2[i].first);
        ans=min(ans,ans1);

        i=0,j=n-1;
         while(v1[i].second==i3) i++;
        while(v1[j].second==i3) j--;
        ans1=max(v1[j].first-v1[i].first,v2[n-2].first-v2[0].first);
        ans=min(ans,ans1);

       i =0,j=n-1;
         while(v1[i].second==i4) i++;
        while(v1[j].second==i4) j--;
        ans1=max(v1[j].first-v1[i].first,v2[n-1].first-v2[1].first);
        ans=min(ans,ans1);


        return ans;

    }
};
