class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        vector<pair<int,int>>s;
        vector<pair<int,int>>difference;
        int i=0;
        for(auto x:points)
        {
        s.push_back({(x[0]+x[1]),i});
             difference.push_back({(x[0]-x[1]),i});
            i++;
        }
        sort(s.begin(),s.end());
        sort(difference.begin(),difference.end());
        int res=INT_MAX;
        i=0;
        for(auto x:points)
        {
            int p=0,q=n-1,r=0,si=n-1;
        if(i==s[p].second)
            p++;
            if(i==s[q].second)
                q--;
            if(i==difference[r].second)
                r++;
            if(i==difference[si].second)
                si--;
        int tempans=max(abs(s[p].first-s[q].first),abs(difference[r].first-difference[si].first));
        res=min(res,tempans);
            i++;
        }
        return res;
    }
};
