class Solution {
public:
    int except(vector<vector<int>> &points,int not_take)
    {
        vector<int> a,b;
        int n=points.size();
        for (int i = 0; i < n; i++) {
            if(i==not_take) continue;
            a.push_back(points[i][0]+points[i][1]);
            b.push_back(points[i][0]-points[i][1]);
        }

        sort(a.begin(),a.end());
        sort(b.begin(),b.end());
        return max(a.back()-a.front(),b.back()-b.front());
    }
    int minimumDistance(vector<vector<int>>& points) {
