class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size ();
        long long minSum, maxSum, minDiff, maxDiff, pt1, pt2, pt3, pt4, ptr1, ptr2;
        vector <long long> pts_remove;

        minSum = maxSum = points [0][0] + points [0][1];
        minDiff = maxDiff = points [0][0] - points [0][1];

        for (int i = 1; i < n; i++)
        {
            long long sum = points [i][0] + points [i][1];
            long long diff = points [i][0] - points [i][1];
            if (sum < minSum)
            {
                minSum = sum;
                pt1 = i;
            }
            else if (sum > maxSum)
            {
                maxSum = sum;
                pt2 = i;
            }
            if (diff < minDiff)
            {
                minDiff = diff;
                pt3 = i;
            }
            else if (diff > maxDiff)
            {
                maxDiff = diff;
                pt4 = i;
            }
        }

        if (maxSum - minSum > maxDiff - minDiff)
        {
            pts_remove.push_back (pt1);
            pts_remove.push_back (pt2);
        }
        else if (maxSum - minSum < maxDiff - minDiff)
        {
            pts_remove.push_back (pt3);
            pts_remove.push_back (pt4);
        }
        else
        {
            pts_remove.push_back (pt1);
            pts_remove.push_back (pt2);
            pts_remove.push_back (pt3);
            pts_remove.push_back (pt4);
        }

        long long maxi = max (maxSum - minSum, maxDiff - minDiff);
        for (int pt: pts_remove)
        {
            if (pt == 0)
            {
                minSum = maxSum = points [1][0] + points [1][1];
                minDiff = maxDiff = points [1][0] - points [1][1];

                for (int i = 2; i < n; i++)
                {
                    if (i == pt)
                        continue;

                    long long sum = points [i][0] + points [i][1];
                    long long diff = points [i][0] - points [i][1];
                    if (sum < minSum)
                        minSum = sum;
                    else if (sum > maxSum)
                        maxSum = sum;
                    if (diff < minDiff)
                        minDiff = diff;
                    else if (diff > maxDiff)
                        maxDiff = diff;
                }
            }
            else
            {
                minSum = maxSum = points [0][0] + points [0][1];
                minDiff = maxDiff = points [0][0] - points [0][1];

                for (int i = 1; i < n; i++)
                {
                    if (i == pt)
                        continue;

                    long long sum = points [i][0] + points [i][1];
                    long long diff = points [i][0] - points [i][1];
                    if (sum < minSum)
                        minSum = sum;
                    else if (sum > maxSum)
                        maxSum = sum;
                    if (diff < minDiff)
                        minDiff = diff;
                    else if (diff > maxDiff)
                        maxDiff = diff;
                }
            }

            long long mini1 = max (maxSum - minSum, maxDiff - minDiff);
            maxi = min (mini1, maxi);
        }

        return maxi;
    }
};
