#include <vector>
#include <algorithm>
#include <climits>
using namespace std;

class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int maxPlus = INT_MIN, secondMaxPlus = INT_MIN;
        int minPlus = INT_MAX, secondMinPlus = INT_MAX;
        int maxMinus = INT_MIN, secondMaxMinus = INT_MIN;
        int minMinus = INT_MAX, secondMinMinus = INT_MAX;

        for (const auto& point : points) {
            int plus = point[0] + point[1];
            int minus = point[0] - point[1];
            updateValues(plus, maxPlus, secondMaxPlus, minPlus, secondMinPlus);
            updateValues(minus, maxMinus, secondMaxMinus, minMinus, secondMinMinus);
        }

        int ans = INT_MAX;
        for (const auto& point : points) {
            int plus = point[0] + point[1];
            int minus = point[0] - point[1];
            int currentMaxPlus = (plus == maxPlus) ? secondMaxPlus : maxPlus;
            int currentMinPlus = (plus == minPlus) ? secondMinPlus : minPlus;
            int currentMaxMinus = (minus == maxMinus) ? secondMaxMinus : maxMinus;
            int currentMinMinus = (minus == minMinus) ? secondMinMinus : minMinus;

            int maxDistance = max(currentMaxPlus - currentMinPlus, currentMaxMinus - currentMinMinus);
            ans = min(ans, maxDistance);
        }

        return ans;
    }

    void updateValues(int value, int& maxVal, int& secondMaxVal, int& minVal, int& secondMinVal) {
        if (value > maxVal) {
            secondMaxVal = maxVal;
            maxVal = value;
        } else if (value > secondMaxVal) {
            secondMaxVal = value;
        }

        if (value < minVal) {
            secondMinVal = minVal;
            minVal = value;
        } else if (value < secondMinVal) {
            secondMinVal = value;
        }
    }
};
