class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        int n = p.size(), x, y, x1, y1, x2, y2, temp, ans = INT_MAX;
        multiset<vector<int>> a, b, c, d;
        for (int i = 0; i < n; i++) {
            x = p[i][0];
            y = p[i][1];
            a.insert({x+y, x, y});
            if (a.size() > 2) {
                a.erase(a.begin());
            }
            b.insert({-x+y, x, y});
            if (b.size() > 2) {
                b.erase(b.begin());
            }
            c.insert({x-y, x, y});
            if (c.size() > 2) {
                c.erase(c.begin());
            }
            d.insert({-x-y, x, y});
            if (d.size() > 2) {
                d.erase(d.begin());
            }
        }
        for (int i = 0; i < n; i++) {
            x = p[i][0];
            y = p[i][1];
            auto it1 = a.find({x+y, x, y});
            if (it1 != a.end()) {
                a.erase(it1);
            }
            auto it2 = b.find({-x+y, x, y});
            if (it2 != b.end()) {
                b.erase(it2);
            }
            auto it3 = c.find({x-y, x, y});
            if (it3 != c.end()) {
                c.erase(it3);
            }
            auto it4 = d.find({-x-y, x, y});
            if (it4 != d.end()) {
                d.erase(it4);
            }
            vector<vector<int>> arr;
            arr.push_back(*prev(a.end()));
            arr.push_back(*prev(b.end()));
            arr.push_back(*prev(c.end()));
            arr.push_back(*prev(d.end()));
            temp = 0;
            for (int i = 0; i < 4; i++) {
                x1 = arr[i][1];
                y1 = arr[i][2];
                for (int j = i+1; j < 4; j++) {
                    x2 = arr[j][1];
                    y2 = arr[j][2];
                    temp = max (temp, abs(x1-x2)+abs(y1-y2));
                }
            }
            ans = min (ans, temp);
            a.insert({x+y, x, y});
            if (a.size() > 2) {
                a.erase(a.begin());
            }
            b.insert({-x+y, x, y});
            if (b.size() > 2) {
                b.erase(b.begin());
            }
            c.insert({x-y, x, y});
            if (c.size() > 2) {
                c.erase(c.begin());
            }
            d.insert({-x-y, x, y});
            if (d.size() > 2) {
                d.erase(d.begin());
            }
        }
        return ans;
    }
};
