class Solution {
public:
    int minimumDistance(vector<vector<int>>& pt) {
        set<pair<int,int>> s1,s2;
        int n=pt.size();
        for(int i=0;i<n;i++){
            vector<int> p=pt[i];
            s1.insert({p[0]+p[1],i});
            s2.insert({p[0]-p[1],i});
        }
        int ans=2e8;
        for(int i=0;i<n;i++){
            vector<int> p=pt[i];
            s1.erase({p[0]+p[1],i});
            s2.erase({p[0]-p[1],i});
            ans=min(ans,max(s1.rbegin()->first-s1.begin()->first,s2.rbegin()->first-s2.begin()->first));
            s1.insert({p[0]+p[1],i});
            s2.insert({p[0]-p[1],i});
        }
        return ans;
    }
};
