#define maxn 111111
pair<int,int> a[maxn], b[maxn];
int n;
class Solution {
public:
    int get_min(int i, pair<int, int> a[]) {
        if (i != a[0].second) return a[0].first;
        return a[1].first;
    }

    int get_max(int i, pair<int, int> a[]) {
        if (i != a[n-1].second) return a[n-1].first;
        return a[n-2].first;
    }

    int calc(int i, pair<int,int> a[]) {
        auto x = get_min(i, a);
        auto y = get_max(i, a);
        return y-x;
    }

    int minimumDistance(vector<vector<int>>& points) {
        n = 0;
        for (auto p: points) {
            a[n] = {p[0]+p[1], n};
            b[n] = {p[0]-p[1], n};
            ++n;
        }
        sort(a, a+n);
        sort(b, b+n);
        int ans = 2000000000;
        for (int i = 0; i < n; ++i) {
            ans = min(ans, max(calc(i, a), calc(i, b)));
        }
        return ans;
    }
};
