class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        multiset<int> sum, diff;
        for (auto p : points) {
            int x = p[0], y = p[1];
            sum.insert(x + y);
            diff.insert(x - y);
        }

        int res = INT_MAX;
        for (auto p : points) {
            int x = p[0], y = p[1];
            sum.erase(sum.find(x + y));
            diff.erase(diff.find(x - y));
            res = min(res, max(*sum.rbegin() - *sum.begin(), *diff.rbegin() - *diff.begin()));
            sum.insert(x + y);
            diff.insert(x - y);
        }

        return res;
    }
};
