class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) 
    {
        int ans=1e9;
        multiset<int>sum,diff;
        for(auto &vec:points)
        {
            sum.insert(vec[0]+vec[1]);
            diff.insert(vec[0]-vec[1]);
        }
        for(auto &vec:points)
        {
            sum.erase(sum.find(vec[0]+vec[1]));
            diff.erase(diff.find(vec[0]-vec[1]));
            ans=min(ans,max(*sum.rbegin()-*sum.begin(),*diff.rbegin()-*diff.begin()));
            sum.insert(vec[0]+vec[1]);
            diff.insert(vec[0]-vec[1]);

        }
        return ans;
    }
};
