class Solution {
public:
    int id[4],x,y;
    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        for (int i=0;i<n;i++){
            x=points[i][0],y=points[i][1];
            points[i][0]=x+y;
            points[i][1]=x-y;
        }
        memset(id,0,sizeof(id));
        for (int i=1;i<n;i++){
            if (points[i][0]<points[id[0]][0]) id[0]=i;
            if (points[i][1]<points[id[1]][1]) id[1]=i;
            if (points[i][0]>points[id[2]][0]) id[2]=i;
            if (points[i][1]>points[id[3]][1]) id[3]=i;
        }
        int ans=1e9,mix,miy,mxx,mxy;
        for (int i=0;i<4;i++){
            mix=1e9,miy=1e9,mxx=-1e9,mxy=-1e9;
            for (int j=0;j<n;j++) if (j!=id[i]){
                mix=min(mix,points[j][0]);
                mxx=max(mxx,points[j][0]);
                miy=min(miy,points[j][1]);
                mxy=max(mxy,points[j][1]);
            }
            ans=min(ans,max(mxx-mix,mxy-miy));
        }
        return ans;
    }
};
