class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {

        int n=points.size();

        vector<vector<int>> p;

        for(int i=0;i<n;i++) {
            int x=points[i][0];
            int y=points[i][1];
            p.push_back({x+y,x-y});
        }

        int rtn=INT_MAX;

        set<pair<int,int>> s,t;

        for(int i=0;i<n;i++) {
            s.insert({p[i][0],i});
            t.insert({p[i][1],i});
        }

        for(int i=0;i<n;i++) {

            auto its=s.find({p[i][0],i});
            auto itt=t.find({p[i][1],i});
            s.erase(its);
            t.erase(itt);
            its=s.end();
            its--;
            int xx=(*its).first - (*s.begin()).first;
            itt=t.end();
            itt--;
            int yy=(*itt).first - (*t.begin()).first;
            int temp=max(xx,yy);
            rtn=min(rtn,temp);
            s.insert({p[i][0],i});
            t.insert({p[i][1],i});

        }



        return rtn;

    }
};
