class Solution {
public:
    int minimumDistance(vector<vector<int>>& a) {
        multiset<int> x, y;
        for (auto &p: a) {
            x.insert(p[0] + p[1]);
            y.insert(p[0] - p[1]);
        }
        int res = INT_MAX;
        for (auto &p: a) {
            x.erase(x.find(p[0] + p[1]));
            y.erase(y.find(p[0] - p[1]));

            int d = max(*prev(x.end()) - *x.begin(), *prev(y.end()) - *y.begin());
            res = min(res, d);

            x.insert(p[0] + p[1]);
            y.insert(p[0] - p[1]);
        }
        return res;
    }
};
