class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        multiset<int>m1,m2;
        for(auto i:p){
            m1.insert(i[0]+i[1]);
            m2.insert(i[0]-i[1]);
        }
        int ans=max(*m1.rbegin()-*m1.begin(),*m2.rbegin()-*m2.begin());
        for(auto i:p){
            int g=i[0]+i[1];
            int k=i[0]-i[1];
            m1.erase(m1.equal_range(g).first);
            m2.erase(m2.equal_range(k).first);
            ans=min(ans,max(*m1.rbegin()-*m1.begin(),*m2.rbegin()-*m2.begin()));
            m1.insert(g);
            m2.insert(k);
        }
        return ans;
    }
};
