#define pii pair<int,int>
#define mp make_pair
#define pb emplace_back

class Solution {
public:
    int n,p;
    vector<pii> add,mns;
    bool check(int x)
    {
        return 1;
    }
    int bs()
    {
        int l=0,r=1e9,mid;
        while(l<=r)
        {
            mid = (l+r)>>1;
            if(check(mid))
            {
                r=mid;
            }
            else l=mid+1;
        }
        return l;
    }
    int getadd()
    {
        int x=0,y=n-1;
        if(add[x].second==p) x++;
        if(add[y].second==p) y--;
        return add[y].first-add[x].first;
    }
    int getmns()
    {
        int x=0,y=n-1;
        if(mns[x].second==p) x++;
        if(mns[y].second==p) y--;
        return mns[y].first-mns[x].first;
    }
    int minimumDistance(vector<vector<int>>& points) {
        int ans = 2e9;
        n=points.size();
        for(int i=0;i<n;i++)
        {
            int a=points[i][0],b=points[i][1];
            add.pb(mp(a+b,i));
            mns.pb(mp(a-b,i));
        }
        sort(add.begin(), add.end());
        sort(mns.begin(), mns.end());
        for(int i=0;i<n;i++)
        {
            p=i;
            ans=min(ans,max(getadd(), getmns()));
        }
        return ans;
    }
};
