class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        for(vector<int> &p:points)
        {
            int x=p[0]+p[1];
            int y=p[0]-p[1];
            p[0]=x;
            p[1]=y;
        }
        multiset<int> sx,sy;
        for(vector<int> &p:points)
        {
            sx.insert(p[0]);
            sy.insert(p[1]);
        }
        int ans=INT_MAX;
        for(vector<int> &p:points)
        {
            sx.erase(sx.find(p[0]));
            sy.erase(sy.find(p[1]));
            int tans=max(*sx.rbegin()-*sx.begin(),*sy.rbegin()-*sy.begin());
            ans=min(ans,tans);
            sy.insert(p[1]);
            sx.insert(p[0]);
        }
        return ans;
    }
};
