class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        multiset<int> s1,s2;
        int n=p.size();
        for(int i=0;i<n;i++){
            s1.insert(p[i][0]+p[i][1]);
            s2.insert(p[i][0]-p[i][1]);
        }
        int ans=1e9;
        for(int i=0;i<n;i++){
            s1.erase(s1.lower_bound(p[i][0]+p[i][1]));
            s2.erase(s2.lower_bound(p[i][0]-p[i][1]));
            ans=min({ans,max(abs(*s1.rbegin()-*s1.begin()),abs(*s2.rbegin()-*s2.begin()))});
            s1.insert(p[i][0]+p[i][1]);
            s2.insert(p[i][0]-p[i][1]);
        }
        return ans;
    }
};
