    int calc(vector<vector<int>>&a){
        int n=a.size();
        int misum=a[0][0]+a[0][1],masum=a[0][0]+a[0][1],midif=a[0][0]-a[0][1],madif=a[0][0]-a[0][1];
        for(int i=0;i<n;i++){
            int sum=a[i][0]+a[i][1];
            int dif=a[i][0]-a[i][1];
            misum=min(misum,sum);
            masum=max(masum,sum);
            midif=min(midif,dif);
            madif=max(madif,dif);
        }
        int ans=max(masum-misum,madif-midif);
        return ans;
    }
