class Solution {
public:

    int calcMax(multiset<int> &a,multiset<int> &b){
        int p = (*a.rbegin())  - (*a.begin());
        int q = (*b.rbegin())  - (*b.begin());
        return max(p,q);
    }

    int minimumDistance(vector<vector<int>>& v) {
        int n = v.size();
        multiset<int> sum, diff;
        for (int i = 0; i < n; i++) {
            int a = v[i][0] + v[i][1];
            int b = v[i][0] - v[i][1];
            sum.insert(a);
            diff.insert(b);
        }
        int ans = INT_MAX;
        for(int i = 0;i<n;i++){
            int a = v[i][0] + v[i][1];
            int b = v[i][0] - v[i][1];
            sum.erase(sum.find(a));
            diff.erase(diff.find(b));
            ans = min(ans,calcMax(sum,diff));
            sum.insert(a);
            diff.insert(b);
        }
        return ans;




    }
};
