class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        int n=p.size();
        multiset<int>ss, ds;
        for(int i=0;i<n;i++) {
            ss.insert(p[i][0]+p[i][1]);
            ds.insert(p[i][0]-p[i][1]);
        }
        int re=INT_MAX;
        for(int i=0;i<n;i++) {
            int cs = p[i][0]+p[i][1];
            int cd = p[i][0]-p[i][1];
            ss.erase(ss.find(cs));
            ds.erase(ds.find(cd));
            int ans=max(*ss.rbegin()-*ss.begin(), *ds.rbegin()-*ds.begin());
            re=min(re, ans);
            ss.insert(cs);
            ds.insert(cd);
        }
        return re;
    }
};
