class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        int n=p.size();
        vector<vector<pair<int,int>>>f(4,vector<pair<int,int>>(n));
        for(int i=0;i<n;i++){
            f[0][i].first=p[i][0]+p[i][1];
            f[1][i].first=p[i][0]-p[i][1];
            f[2][i].first=-p[i][0]+p[i][1];
            f[3][i].first=-p[i][0]-p[i][1];
            for(int j=0;j<4;j++)f[j][i].second=i;
        }
        for(int i=0;i<4;i++)sort(f[i].begin(),f[i].end());
        int mx=-1,pos;
        for(int i=0;i<4;i++){
            if(f[i].back().first-f[i].front().first>mx){
                mx=f[i].back().first-f[i].front().first;
                pos=i;
            }
        }
        auto check=[&](int pos){
            vector<vector<int>>f(4);
            for(int i=0;i<n;i++){
                if(i==pos)continue;
                f[0].push_back(p[i][0]+p[i][1]);
                f[1].push_back(p[i][0]-p[i][1]);
                f[2].push_back(-p[i][0]+p[i][1]);
                f[3].push_back(-p[i][0]-p[i][1]);
            }
            for(int i=0;i<4;i++)sort(f[i].begin(),f[i].end());
            int mx=-1;
            for(int i=0;i<4;i++){
                if(f[i].back()-f[i].front()>mx){
                    mx=f[i].back()-f[i].front();
                }
            }
            return mx;
        };
        return min(check(f[pos].front().second),check(f[pos].back().second));
    }
};
