class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        def Ans(a, b, c , d):
            # a, b is the max and min of x+y, c,d is the max and min of x-y

            return max(a-b, c-d)

        points_a = sorted([p[0]+p[1] for p in points])
        points_b = sorted([p[1]-p[0] for p in points])

        sum_min, sum_min2 = points_a[0], points_a[1]
        sum_max, sum_max2 = points_a[-1], points_a[-2]

        diff_min, diff_min2 = points_b[0], points_b[1]
        diff_max, diff_max2 = points_b[-1], points_b[-2]

        ans = Ans(sum_max, sum_min, diff_max, diff_min)
        n = len(points)
        for i in range(n):
            c_sum = points[i][0] + points[i][1]
            c_diff = points[i][1] - points[i][0]
            if c_sum == sum_min:
                if c_diff == diff_min:
                    ans = min(ans, Ans(sum_max, sum_min2, diff_max, diff_min2))
                elif c_diff == diff_max:
                    ans = min(ans, Ans(sum_max, sum_min2, diff_max2, diff_min))
                else:
                    ans = min(ans, Ans(sum_max, sum_min2, diff_max, diff_min))
            elif c_sum == sum_max:
                if c_diff == diff_min:
                    ans = min(ans, Ans(sum_max2, sum_min, diff_max, diff_min2))
                elif c_diff == diff_max:
                    ans = min(ans, Ans(sum_max2, sum_min, diff_max2, diff_min))
                else:
                    ans = min(ans, Ans(sum_max2, sum_min, diff_max, diff_min))
            else:
                if c_diff == diff_min:
                    ans = min(ans, Ans(sum_max, sum_min, diff_max, diff_min2))
                elif c_diff == diff_max:
                    ans = min(ans, Ans(sum_max, sum_min, diff_max2, diff_min))
        return ans
